public class Q1 {
    public static void printPattern(int n) {
      // Write Code Here
      for(int i=0; i<n;i++){
          for(int j=0; j<n;j++){
              if(i>j){
                  System.out.print(" ");
              }
              else{
                  System.out.print("*");
              }
          }
          System.out.println();
      }
    }
    // DO NOT MODIFY BELOW THIS
}

