public class Q2 {
    public static void shearPattern(int n) {
        // Write Code Here
        for (int i = 0; i < n; i++) {
            for (int k = 0; k < i; k++) {
                System.out.print(" ");
            }
            for (int j = 1; j <= n; j++) {
                System.out.print(2 * j - 1 + " ");
            }
            System.out.println();
        }
    }
    // DO NOT MODIFY BELOW THIS
}

Q3:
public class Q3 {
    public static void rightPyramidPattern(int n) {
        // Write Code Here
        for (int i = 0; i < 2 * n - 1; i++) {
            if (i < n) {
                for (int j = 0; j <= i; j++) {
                    System.out.print(2 * j + 1 + " ");
                }
            } else {
                for (int j = 0; j < 2 * n - 1 - i; j++) {
                    System.out.print(2 * j + 1 + " ");
                }
            }
            System.out.println();
        }
    }
    // DO NOT MODIFY BELOW THIS
}
