def traingle_pattern(n: int):
    for i in range(n):
        for j in range(n):
            if j<i:
                print(" ", end='')
            else:
                print('*', end='')
        print()

Q2:
def shear_pattern(n: int):
    for i in range(n):
        print(" "*i, end="")
        for j in range(1, n+1):
            print(2*j-1, end=' ')
        print()

Q3:
def right_pyramid_pattern(n: int):
    for i in range(2*n):
        if i<n:
            for j in range(i+1):
                print(2*j+1, end=" ")
        else:
            for j in range(2*n-i-1):
                print(2*j+1, end=" ")
        print()
